import React from 'react'
import ReactDOM from 'react-dom'

class DropDown extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: ' '};
  }

  handleChange = (event) => {
    this.setState({value: event.target.value}, () => this.props.onSubmitDropDown(this.state.value))
  }


  render(){
    return(
      <div className="container_5" >
          <p>Type</p>
          <select id="service2" value={this.state.value} onChange={this.handleChange}>
            <option style={{display:"none"}} value=""></option>
            <option value="Account">Account</option>
            <option value="Billing">Billing</option>
          </select>
          <p>Type</p>
          <select id="service2" value={this.state.value} onChange={this.handleChange}>
            <option style={{display:"none"}} value=""></option>
            <option value="Account">Activation</option>
            <option value="Billing">Free tier</option>
          </select>
      </div>
    );
  }
}

export default DropDown;
